class BG extends Phaser.TileSprite{constructor({game:t,x:s,y:e,width:i,height:o,key:h,frame:c}){super(t,s,e,i,o,h,c),this.game.add.existing(this)}renew(){this.tilePosition.y+=2}}class Timer extends Phaser.Text{constructor({game:t,x:s,y:e,text:i,style:o}){super(t,s,e,i,o),this.game.add.existing(this),this.anchor.setTo(0,.5),this.game.time.events.loop(Phaser.Timer.SECOND,this.count,this),this.second=0,this.minute=0}count(){++this.second,this.second%60==0&&(++this.minute,this.second=0),this.second<10?this.setText("time: "+this.minute+":0"+this.second):this.setText("time: "+this.minute+":"+this.second)}}class Score extends Phaser.Image{constructor({game:t,x:s,y:e,key:i,frame:o}){super(t,s,e,i,o),this.game.add.existing(this),this.anchor.setTo(.5,.5),this.total_score=0,this.perfect_score=0,this.precision=0,this.combo=0,this.pefect=0,this.great=0,this.good=0,this.bad=0,this.miss=0,this.total_score_txt=new Txt({game:this.game,x:240,y:36,text:"score: 0",style:{fill:"#ffffff"}})}point_upgrade(t){switch(t){case 300:++this.perfect,++this.combo;break;case 200:++this.great,++this.combo;break;case 100:++this.good,++this.combo;break;case 50:++this.bad,this.combo=0;break;default:++this.miss,this.combo=0}this.total_score+=t,this.perfect_score+=300,this.precision=this.total_score/this.perfect_score,this.total_score_txt.setText("score: "+this.total_score)}bonus_upgrade(t){this.total_score+=t,this.perfect_score+=t,this.total_score_txt.setText("score: "+this.total_score)}}