class BG extends Phaser.TileSprite{constructor({game:s,x:e,y:t,width:i,height:o,key:h,frame:r}){super(s,e,t,i,o,h,r),this.game.add.existing(this)}renew(){this.tilePosition.y+=2}}class Score extends Phaser.Image{constructor({game:s,x:e,y:t,key:i,frame:o}){super(s,e,t,i,o),this.total_score=0,this.perfect_score=0,this.precision=0,this.combo=0,this.pefect=0,this.great=0,this.good=0,this.bad=0,this.miss=0}upgrade(s){switch(s){case 300:++this.perfect,++this.combo;break;case 200:++this.great,++this.combo;break;case 100:++this.good,++this.combo;break;case 50:++this.bad,this.combo=0;break;default:++this.miss,this.combo=0}this.total_score+=s,this.perfect_score+=300,this.precision=this.total_score/this.perfect_score}}