class Level_Scene extends Phaser.State{init(){this.game.bgv.addToWorld(-400,0,0,0,1,1)}create(){const t=this.game,e=button_config.level_scene;this.enter_button=new Button({game:t,callback:this.enter_scene,callbackContext:this,form:{fill:"#008cff"}},e.enter_button),this.exit_button=new Button({game:t,callback:this.exit_scene,callbackContext:this,form:{fill:"#008cff"}},e.exit_button);const l=this.level_buttons=new Buttons({game:t,pre_callback:this.choose_pre_level,nxt_callback:this.choose_nxt_level,callbackContext:this},e.level_buttons),a=l.normal_style;l.addMultiple([new Button({game:t,callback:this.choose_easy_level,callbackContext:this,form:l.active_style},e.easy_level_button),new Button({game:t,callback:this.choose_normal_level,callbackContext:this,form:a},e.normal_level_button),new Button({game:t,callback:this.choose_hard_level,callbackContext:this,form:a},e.hard_level_button)])}enter_scene(){const t=this.game,e=t.song_audios[t.active_song];this.camera.fade(0,1e3,!1),e.fadeOut(1e3),e.onFadeComplete.add(()=>{t.state.start("Game_Start")},this)}exit_scene(){this.game.state.start("Song_Scene")}choose_pre_level(){const t=this.level_buttons,e=t.length;t.getAt(t.active).text.setStyle(t.normal_style),t.active=(t.active-1+e)%e,this.game.active_level=t.active,t.getAt(t.active).text.setStyle(t.active_style)}choose_nxt_level(){const t=this.level_buttons;t.getAt(t.active).text.setStyle(t.normal_style),t.active=(t.active+1)%t.length,this.game.active_level=t.active,t.getAt(t.active).text.setStyle(t.active_style)}choose_easy_level(){const t=this.level_buttons;t.getAt(t.active).text.setStyle(t.normal_style),t.active=0,this.game.active_level=t.active,t.getAt(t.active).text.setStyle(t.active_style)}choose_normal_level(){const t=this.level_buttons;t.getAt(t.active).text.setStyle(t.normal_style),t.active=1,this.game.active_level=t.active,t.getAt(t.active).text.setStyle(t.active_style)}choose_hard_level(){const t=this.level_buttons;t.getAt(t.active).text.setStyle(t.normal_style),t.active=2,this.game.active_level=t.active,t.getAt(t.active).text.setStyle(t.active_style)}}